    size: {
      type: String,
      default: 'm',
      validator: function (value) {
        return ['xxl', 'xl', 'l', 'm', 's'].includes(value);
      }
    }
  },
  computed: {
    currentSize () {
      return iconInfo[this.size].size;
    },
    path () {
      return iconInfo[this.size].path;
    }
  }
};
</script>
